docker run -it --rm --name certbot -v "/root/docker/letsencrypt/etc/:/etc/letsencrypt" -v "/root/docker/letsencrypt/var/:/var/lib/letsencrypt" -v "/root/docker/letsencrypt/log/:/var/log/letsencrypt" -v "/root/docker/cloudflare.ini:/.secrets/cloudflare.ini" certbot/dns-cloudflare certonly --dns-cloudflare-credentials /.secrets/cloudflare.ini --dns-cloudflare-propagation-seconds 60 --server https://acme-v02.api.letsencrypt.org/directory -d 'a.b.com'
